import React, { useState, useEffect } from 'react';
import LandingPage from './components/LandingPage';
import QuizPage from './components/QuizPage';
import ResultPage from './components/ResultPage';
import LoadingSpinner from './components/LoadingSpinner';
import Footer from './components/Footer';
import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [answers, setAnswers] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [savedProgress, setSavedProgress] = useState(null);

  // Check for saved progress on component mount
  useEffect(() => {
    const savedData = localStorage.getItem('finein-progress');
    if (savedData) {
      try {
        const parsedData = JSON.parse(savedData);
        setSavedProgress(parsedData);
      } catch (error) {
        console.error('Error parsing saved progress:', error);
        localStorage.removeItem('finein-progress');
      }
    }
  }, []);

  const startQuiz = (resumeProgress = false) => {
    if (resumeProgress && savedProgress) {
      setAnswers(savedProgress.answers || {});
    } else {
      setAnswers({});
      localStorage.removeItem('finein-progress');
    }
    setCurrentPage('quiz');
  };

  const submitAnswers = (userAnswers) => {
    setAnswers(userAnswers);
    setIsLoading(true);

    // Save progress to localStorage
    localStorage.setItem('finein-progress', JSON.stringify({
      answers: userAnswers,
      timestamp: new Date().toISOString()
    }));

    // Simulasi loading untuk pengalaman pengguna yang lebih baik
    setTimeout(() => {
      setIsLoading(false);
      setCurrentPage('result');
    }, 1500);
  };

  const restartQuiz = () => {
    setAnswers({});
    localStorage.removeItem('finein-progress');
    setCurrentPage('landing');
  };

  const saveAndExit = (userAnswers) => {
    localStorage.setItem('finein-progress', JSON.stringify({
      answers: userAnswers,
      timestamp: new Date().toISOString()
    }));
    setCurrentPage('landing');
  };

  return (
    <div className="App">
      {currentPage === 'landing' && (
        <>
          <LandingPage 
            onStart={() => startQuiz(false)} 
            onResume={() => startQuiz(true)}
            hasSavedProgress={savedProgress !== null}
          />
          <Footer />
        </>
      )}

      {currentPage === 'quiz' && (
        <>
          <QuizPage 
            onSubmit={submitAnswers} 
            initialAnswers={answers}
            onSaveAndExit={saveAndExit}
          />
          <Footer />
        </>
      )}

      {isLoading && <LoadingSpinner />}

      {currentPage === 'result' && !isLoading && (
        <>
          <ResultPage answers={answers} onRestart={restartQuiz} />
          <Footer />
        </>
      )}
    </div>
  );
}

export default App;
