import React from 'react';

function ProgressBar({ currentQuestion, totalQuestions, answeredQuestions }) {
  const progress = (currentQuestion / totalQuestions) * 100;
  const completionRate = (answeredQuestions / totalQuestions) * 100;

  return (
    <div className="progress-container">
      <div className="progress-info">
        <span className="progress-current">Pertanyaan {currentQuestion}</span>
        <span className="progress-total">dari {totalQuestions}</span>
      </div>

      <div className="progress-bar-container">
        <div 
          className="progress-bar" 
          style={{ width: `${progress}%` }}
          aria-valuenow={progress}
          aria-valuemin="0"
          aria-valuemax="100"
        ></div>

        <div 
          className="progress-completion" 
          style={{ width: `${completionRate}%` }}
          aria-valuenow={completionRate}
          aria-valuemin="0"
          aria-valuemax="100"
        ></div>
      </div>

      <div className="progress-stats">
        <span className="progress-percentage">{Math.round(progress)}% selesai</span>
        <span className="completion-percentage">{Math.round(completionRate)}% terjawab</span>
      </div>
    </div>
  );
}

export default ProgressBar;
